function test(){
	function log(message){
		console.log("----------- nuf_event test -----------\n"+message);
	}
	var 
		EventHandler = require("../lib/event.js"),
		testObject = {},
		eventData = {
			"testString":"this is a test string",
			"number": 123456
		}
	;
	log("starting test");
	EventHandler.addEventEmitter(testObject);
	testObject.on(
		"testEvent",
		function(event){
			log(
				"Received event on testObject, \n"+
				"Event:\n"+JSON.stringify(event, undefined, "\t")
			);
		}
	);
	testObject.on(
		"eventDispatched",
		function(event){
			log("received eventDispatched:"+"Event:\n"+JSON.stringify(event, undefined, "\t"));
		}
	);
	testObject.on.dispatchEvent("testEvent", eventData);
}

module.exports = test;